import { Component, Inject, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { CalculoDataService } from '../_data-services/calculo.data-service';

@Component({
  selector: 'app-home',
  templateUrl: './calculo.component.html',
  styleUrls: ['./calculo.component.css']
})
export class CalculoComponent implements OnInit {

  calculo: any = {};

  constructor(private calculoDataService: CalculoDataService) { }

  ngOnInit() {

  }

  calcular() {
    this.calculoDataService.post(this.calculo).subscribe(data => {
      if (data) {
        alert('Pagamento cadastrado com sucesso');
        this.calculo = {};
      } else {
        alert('Por favor, realizar o preenchimento de todos os campos!');
      }
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

}
<h1>Calculadora CDB</h1>


<form>

  <div class="form-group">
    <label>Valor:</label>
    <input type="number" class="form-control" id="InputValor" [(ngModel)]="calculo.vi" name="valor" required>
  </div>
  <div class="form-group">
    <label>Prazo:</label>
    <input type="number" class="form-control" id="InputPrazo" [(ngModel)]="calculo.prazo" name="prazo" required>
  </div>


  <button type="submit" class="btn btn-primary" (click)="calcular()" >Cadastrar</button>

</form>
