import { Component, Inject, OnInit } from '@angular/core';
import { HomeDataService } from '../_data-services/home.data-service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  calculo: any = {};
  resultado: any = {};

  constructor(private homeDataService: HomeDataService) { }

  ngOnInit() {

  }

  calcular() {
    this.homeDataService.post(this.calculo).subscribe(data => {
      if (data) {
        alert('Calculo realizado com sucesso!');
        this.resultado = data;
      } else {
        alert('Por favor, realizar o preenchimento de todos os campos!');
      }
    }, error => {
      console.log(error);
      alert('erro interno do sistema');
    })
  }

}

<h1>Calculadora CDB</h1>


<form>

  <div class="form-group">
    <label>Valor:</label>
    <input type="number" class="form-control" id="InputValor" [(ngModel)]="calculo.valorInicial" name="valor">
  </div>
  <div class="form-group">
    <label>Prazo:</label>
    <input type="number" class="form-control" id="InputPrazo" [(ngModel)]="calculo.prazo" name="prazo">
  </div>


  <button type="submit" class="btn btn-primary" (click)="calcular()"> Calcular</button>

  <div class="form-group">
    <label>Resultado Bruto:</label>
    <input type="number" class="form-control" id="InputBruto" [(ngModel)]="resultado.bruto" name="bruto">
  </div>
  <div class="form-group">
    <label>Resultado Liquido:</label>
    <input type="number" class="form-control" id="InputLiquido" [(ngModel)]="resultado.liquido" name="liquido" >
  </div>
</form>
